import React, { useState } from 'react';

import Button from '../../UI/Button/Button';
import styled from 'styled-components';

const FormControled = styled.div`
    margin: 0.5rem 0;

  & label {
    font-weight: bold;
    display: block;
    margin-bottom: 0.5rem;
  }

  & input {
    display: block;
    width: 100%;
    border: 1px solid ${props => props.invalid ? 'red' : '#ccc'};
    background-color: ${props => props.invalid ? 'rgb(252, 218, 218)' : 'transparent'}
    font: inherit;
    line-height: 1.5rem;
    padding: 0 0.25rem;
  }

  & input:focus {
    outline: none;
    background: #fad0ec;
    border-color: #8b005d;
  }

  /* 這邊是配合使用者是否輸入資訊，而動態加入的invalid class */
  &.invalid label {
    color: red;
  }
  &.invalid input {
    border-color: red;
    background: rgb(252, 218, 218);
  }
`;

const CourseInput = props => {
  ////取得使用者輸入資料
  const [enteredValue, setEnteredValue] = useState('');

  //// 設定使用者是否有輸入的狀態，預設一開始為有輸入，所以寫true
  const [isValid , setIsValid] = useState(true);

  ////使用者輸入時會觸發以下function
  const goalInputChangeHandler = event => {
    // 使用者有輸入東西的話，isValid狀態就更新為true，這樣下面的樣式判斷就不會讓輸入框出現紅色邊框
    setIsValid(true);
    setEnteredValue(event.target.value);
  };

  ////使用者點擊送出按鈕時會觸發以下function
  const formSubmitHandler = event => {
    event.preventDefault();
    // 如果輸入的長度扣除掉前後的空格還是為0的話(沒輸入東西)，就return空值，讓下面的props.onAddGoal無法被執行
    if(enteredValue.trim().length === 0){
      // 因為使用者沒輸入東西，所以將驗證(isValid)設定為false，及代表不通過
      setIsValid(false);
      return;
    }
    //如果有資料的話，就把資料透過參數的方式傳到App.js裡
    props.onAddGoal(enteredValue);
    //送出後將input裡面清空
    setEnteredValue("");
  };


  return (
    <form onSubmit={formSubmitHandler}>
      {/* className判斷是，如果使用者沒輸入東西，就會先跑到上面的if判斷，因此isValid的狀態就會被設為false。
        接著這邊再用三元運算子判斷是否要加入invalid class */}
      <FormControled invalid={!isValid}>
        <label>Course Goal</label>
        <input
          type="text"
          onChange={goalInputChangeHandler}
          value={enteredValue}
        />
      </FormControled>
      <Button type="submit">Add Goal</Button>
    </form>
  );
};

export default CourseInput;
